    def dateNList(self):
        for date in self.j_dic.keys():
            temp = []
            for url in self.j_dic[date].keys():
                for comment in self.j_dic[date][url]["comments"]:
                    comment = self.remove_emoji(comment)
                    temp.append(comment)
            self.result_dic[date] = temp
        return self.result_dic